mapper.select:
select id, create_time, member_username, product_real_price, product_count, return_name, status, handle_time from oms_order_return_apply where
and id=#{queryParam.id}
and status = #{queryParam.status}
and handle_man = #{queryParam.handleMan}
and create_time like concat(#{queryParam.createTime}, '%')
and handle_time like concat(#{queryParam.handleTime}, '%')
and (return_name like concat("%", #{queryParam.receiverKeyword}, "%") or return_phone like concat("%", #{queryParam.receiverKeyword}, "%"))
